(24, '<p><span style="color:rgb(45, 108, 197); font-family:verdana,arial,sans-serif; font-size:19px">Osiągnięcia indywidualne</span></p>\n\n<p><strong><span style="color:#2d6cc5">JAKUB OLSZEWSKI i PAWEŁ DYZIA (klasa 3A)</span></strong></p>\n\n<p style="text-align:justify">zajęli II miejsce w kategorii Poziom Podstawowy w XIII Zagłębiowskim Konkursie Matematycznym 2013.</p>\n\n<p><strong><span style="color:#2d6cc5">Karolina Zielińska kl.1c i Sebastian Osuch kl. 1d</span></strong></p>\n\n<p style="text-align:justify">zajęli ex aequo II miejsce w kategorii szk&oacute;ł ponadgimnazjalnych w IX Edycji Wojew&oacute;dzkiego Konkursu Astronomicznego 2013.</p>\n\n<p><strong><span style="color:#2d6cc5">Tomasz Smolec</span></strong></p>\n\n<p style="text-align:justify">zajął I miejsce w konkursie fotograficznym ŚLĄSKIE BIOR&Oacute;ŻNORODNE 2013</p>\n\n<p><strong><span style="color:#2d6cc5">KRZYSZTOF WIĘCKOWSKI (4A) I BARTOSZ RAKOCZY (4D)</span></strong></p>\n\n<p style="text-align:justify">zostali wyr&oacute;żnieni nagrodą II stopnia w konkursie &quot;KANGUR MATEMATYCZNY 2012&quot;.</p>\n\n<p><span style="color:#2d6cc5"><strong>KAMIL SROKA (4B) i PIOTR KLESZCZ (4A)</strong></span></p>\n\n<p style="text-align:justify">zostali finalistami XVI Konkursu Wiedzy Technicznej</p>\n\n<p><span style="color:#2d6cc5"><strong>KRZYSZTOF NOGAJ (IE)</strong></span></p>\n\n<p style="text-align:justify">III miejsce w XI EDYCJI ZAGŁĘBIOWSKIEGO KONKURSU GRAFIKI KOMPUTEROWE</p>\n\n<p><span style="color:#2d6cc5"><strong>TOMASZ STAŚKO (3G) i MATEUSZ PINDEL (3F)</strong></span></p>\n\n<p style="text-align:justify">II miejsce w Zagłębiowskim Konkursie Matematycznym (dwuosobowy) 2011/2012</p>\n\n<p><span style="color:#2d6cc5"><strong>MATEUSZ CIEŚLA (3A) i JAKUB OLSZEWSKI (2A)</strong></span></p>\n\n<p style="text-align:justify">III miejsce w Zagłębiowskim Konkursie Matematycznym (dwuosobowy) 2011/2012</p>\n\n<p><span style="color:#2d6cc5"><strong>PIOTR MACHURA (1A)</strong></span></p>\n\n<p style="text-align:justify">II miejsce w diecezjalnym etapie XVI Konkursu Wiedzy Biblijnej</p>\n\n<p><span style="color:#2d6cc5"><strong>ILONA CHYLIŃSKA i AGNIESZKA KOCHMAN (3F) i ADRIAN CIENIUCH (1F)</strong></span></p>\n\n<p style="text-align:justify">udział w finale og&oacute;lnopolskiego konkursu MISTRZ SIECI</p>\n\n<p><span style="color:#2d6cc5"><strong>ILONA CHYLIŃSKA (3F)</strong></span></p>\n\n<p style="text-align:justify">I miejsce w X Międzyszkolnym Konkursie Czytelniczym &bdquo;W świecie Szekspira&rdquo;</p>\n\n<p><span style="color:#2d6cc5"><strong>AGNIESZKA KOCHMAN (3F)</strong></span></p>\n\n<p style="text-align:justify">wyr&oacute;żnienie w X Międzyszkolnym Konkursie Czytelniczym &bdquo;W świecie Szekspira&rdquo;</p>\n\n<p><span style="color:#2d6cc5"><strong>PIOTR KOZŁOWSKI (2E)</strong></span></p>\n\n<p style="text-align:justify">wyr&oacute;żnienie w Og&oacute;lnopolskim Konkursie Energetycznym</p>\n\n<p><span style="color:#2d6cc5"><strong>KAMIL SROKA (3B) i PIOTR KLESZCZ (3A) i KRZYSZTOF GRZANKA (3A)</strong></span></p>\n\n<p style="text-align:justify">XV Konkurs Wiedzy Technicznej</p>\n\n<p style="text-align:justify">Kamil Sroka z 3B został finalistą Konkursu, a Piotr Kleszcz z 3A została laureatem Konkursu (zajął 3 miejsce w klasyfikacji generalnej).</p>\n\n<p><span style="color:#2d6cc5"><strong>KAMIL SROKA (3B) i PIOTR KLESZCZ (3A) i KRZYSZTOF GRZANKA (3A)</strong></span></p>\n\n<p style="text-align:justify">XXXIV Olimpiada Wiedzy Elektrycznej i Elektronicznej</p>\n\n<p style="text-align:justify">Kamil Sroka zajął 7 miejsce w grupie mechatronicznej i tym samym zdobył tytuł finalisty OOWEE. Uzyskał zwolnienie z pisemnej części egzaminu potwierdzającego kwalifikacje zawodowe oraz wstęp do większości uczelni technicznych</p>\n\n<p><span style="color:#2d6cc5"><strong>PATRYK REJCHERT i JAKUB JASIŃSKI</strong></span></p>\n\n<p style="text-align:justify">II miejsce w Międzyszkolnym Konkursie Pierwszej Pomocy, 2010/2011</p>\n\n<p><span style="color:#2d6cc5"><strong>JAKUB JASIŃSKI</strong></span></p>\n\n<p style="text-align:justify">III miejsce w Międzyszkolnym Konkursie Ekologicznym &quot;Człowiek niszczycielem czy budowniczym Ziemi&quot;., 2010/2011</p>\n\n<p><span style="color:#2d6cc5"><strong>SZYMON KRAKOWSKI</strong></span></p>\n\n<p style="text-align:justify">I miejsce w Międzyszkolnym Konkursie Fotograficznym &quot;Budząca się przyroda-2011&quot;</p>\n\n<p><span style="color:#2d6cc5"><strong>AGNIESZKA REJDAK</strong></span></p>\n\n<p style="text-align:justify">wyr&oacute;żnienie w Międzyszkolnym Konkursie Fotograficznym &quot;Budząca się przyroda-2011&quot;</p>\n\n<p><span style="color:#2d6cc5"><strong>MARTA GOŁUSZKO (II F) PAWEŁ LASEK(III F)</strong></span></p>\n\n<p style="text-align:justify">II miejsce w Zagłębiowskim Konkursie Matematycznym (dwuosobowy) 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>MARCIN GRISZBACHER (III F) WOJCIECH MAZUR (II A)</strong></span></p>\n\n<p style="text-align:justify">III miejsce w Zagłębiowskim Konkursie Matematycznym (dwuosobowy) 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>BARTOSZ RAKOCZY (II D) PAWEŁ KLESZCZ (II A) A. KORDOS (II D)</strong></span></p>\n\n<p style="text-align:justify">Odpowiednio I, II i III miejsce</p>\n\n<p style="text-align:justify">VI Międzyszkolny Konkurs Matematyczny Klas Drugich Szk&oacute;ł Zawodowych 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>MARCIN GOŁĄB</strong></span></p>\n\n<p style="text-align:justify">III miejsce</p>\n\n<p style="text-align:justify">Międzyszkolny Konkurs &quot;Odnawialne źr&oacute;dła energii&quot; 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>GRZEGORZ PODSIADŁO</strong></span></p>\n\n<p style="text-align:justify">I miejsce w Międzyszkolnym Konkursie dotyczącym problem&oacute;w logistyki i utylizacji odpad&oacute;w niebezpiecznych 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>DANIEL GIEREK MATEUSZ GOŁĘBIEWSKI</strong></span></p>\n\n<p style="text-align:justify">III miejsce w Og&oacute;lnopolskim Konkursie &quot;Z energetyką w przyszłość&quot; 2009/2010</p>\n\n<p><span style="color:#2d6cc5"><strong>Konrad Wilk, Michał Janik, Michał Kaczmarek, Łukasz Skonieczny Konrad Sroka, Konrad Wilk</strong></span></p>\n\n<p style="text-align:justify">XXXVI Olimpiada Wiedzy Technicznej:</p>\n\n<p style="text-align:justify">I etap szkolny 25 uczestnik&oacute;w</p>\n\n<p style="text-align:justify">II etap - 5 zawodnik&oacute;w, w tym Konrad Wilk wyr&oacute;żniony przez śląski oddział OWT za bardzo dobry wynik w II etapie</p>\n\n<p style="text-align:justify">III etap -nie startowaliśmy</p>\n\n<p><span style="color:#2d6cc5"><strong>ŁUKASZ SKONIECZNY (4 miejsce), MICHAŁ KACZMAREK (6 miejsce)</strong></span></p>\n\n<p style="text-align:justify">XIV Konkurs Wiedzy Technicznej</p>\n\n<p style="text-align:justify">etap szkolny 15 uczestnik&oacute;w</p>\n\n<p>finał na UŚ w Sosnowcu -3 zawodnik&oacute;w ( Michał Kaczmarek, Łukasz Skonieczny, Marcin Ros&oacute;ł</p>\n\n<p style="text-align:justify">Finaliści Łukasz Skonieczny (4 miejsce), Michał Kaczmarek (6 miejsce)</p>\n\n<p><span style="color:#2d6cc5"><strong>drużyna Michał Kaczmarek, Michał Janik, Łukasz Skonieczny, Marcin Ros&oacute;ł</strong></span></p>\n\n<p style="text-align:justify">XXXIII Og&oacute;lnopolska Olimpiada Wiedzy Elektrycznej i Elektronicznej</p>\n\n<p style="text-align:justify">etap szkolny 8 uczestnik&oacute;w</p>\n\n<p style="text-align:justify">finał w Krośnie 4 zawodnik&oacute;w (drużyna Michał Kaczmarek, Michał Janik, Łukasz Skonieczny, Marcin Ros&oacute;ł) laureat Michał Kaczmarek</p>\n\n<p><span style="color:#2d6cc5"><strong>GRZEGORZ PODSIADŁO</strong></span></p>\n\n<p style="text-align:justify">I miejsce w międzyszkolnym konkursie na pracę o zagrożeniach w środowisku &quot;Problemy logistyki i utylizacji odpad&oacute;w niebezpiecznych&quot;. Konkurs odbył się pod honorowym patronatem Deputowanego do Parlamentu Europejskiego prof. Adama Gierka.Uczeń został nagrodzony wyjazdem do Parlamentu Europejskiego w Brukseli, 2010r</p>\n\n<p><span style="color:#2d6cc5"><strong>DANIEL GIEREK i MATEUSZ GOŁĘBIEWSKI</strong></span></p>\n\n<p style="text-align:justify">III miejsce w og&oacute;lnopolskim konkursie Z energetyką w przyszłość. Honorowy patronat nad konkursem objął przewodniczący Parlamentu Europejskiego prof. Jerzy Buzek, 2010</p>\n\n<p><span style="color:#2d6cc5"><strong>DAWID GERLICKI</strong></span></p>\n\n<p style="text-align:justify">I miejsce, w międzyszkolnym konkursie fotograficznym &quot;Sosnowiec miastem dbającym o środowisko&quot;, 2010</p>\n\n<p><span style="color:#2d6cc5"><strong>BARTOSZ SZYMAŃSKI</strong></span></p>\n\n<p style="text-align:justify">II miejsce, w międzyszkolnym konkursie fotograficznym &quot;Sosnowiec miastem dbającym o środowisko&quot;, 2010</p>\n\n<p><span style="color:#2d6cc5"><strong>AGNIESZKA STEROSZYŃSKA</strong></span></p>\n\n<p style="text-align:justify">III miejsce, w międzyszkolnym konkursie fotograficznym &quot;Sosnowiec miastem dbającym o środowisko&quot;, 2010</p>\n\n<p><span style="color:#2d6cc5"><strong>MATEUSZ GOŁĘBIEWSKI</strong></span></p>\n\n<p style="text-align:justify">III miejsce w miejskim konkursie na pracę o tematyce ekologicznej &quot;Odnawialne źr&oacute;dła energii&quot;, 2010</p>\n\n<p style="text-align:justify">I miejsce w międzyszkolnym konkursie &quot;Recykling i segregacja odpad&oacute;w komunalnych&quot; zorganizowanym pod patronatem prof. Adama Gierka, 2009</p>\n\n<p><span style="color:#2d6cc5"><strong>BARTOSZ RAKOCZY (I D) WOJCIECH MAZUR (I A)</strong></span></p>\n\n<p style="text-align:justify">p&oacute;łfinał</p>\n\n<p style="text-align:justify">XXX Wojew&oacute;dzki Dwuosobowy Turniej z fizyki dla klas I 2008/2009</p>\n\n<p><span style="color:#2d6cc5"><strong>MATEUSZ GOŁĄB (I B)</strong></span></p>\n\n<p style="text-align:justify">I miejsce w Międzyszkolnym Konkursie &quot;Recykling i segregacja odpad&oacute;w&quot; 2008/2009</p>\n\n<p><span style="color:#2d6cc5"><strong>BARTOSZ RAKOCZY (I D)</strong></span></p>\n\n<p style="text-align:justify">finalista XVII Wojew&oacute;dzkiego Konkursu Matematycznego &quot;Nudna matematyka&quot; 2008/2009</p>\n\n<p><span style="color:#2d6cc5"><strong>DAWID BAGIŃSKI (IV C) MARCIN GRISZBACHER (II F) RAFAŁ LOSKA (II D) WOJCIECH MAZUR (I A)</strong></span></p>\n\n<p style="text-align:justify">nagrody II stopnia</p>\n\n<p style="text-align:justify">Międzynarodowy Konkurs Matematyczny &quot;Kangur 2009&quot;</p>\n\n<p><span style="color:#2d6cc5"><strong>RAFAŁ LOSKA (II D) MACIEJ WITECKI (II E)</strong></span></p>\n\n<p style="text-align:justify">I miejsce w Zagłębiowskim Konkursie Matematycznym (dwuosobowy) 2008/2009</p>\n', 0);
